import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddPet from './AddPet';
import EditPet from './EditPet';

const AdminDashboard = () => {
  const [pets, setPets] = useState([]);
  const [editingPetId, setEditingPetId] = useState(null);

  const fetchPets = async () => {
    const res = await axios.get(`${process.env.REACT_APP_API_URL}/api/pets`);
    setPets(res.data);
  };

  useEffect(() => {
    fetchPets();
  }, []);

  const handleDelete = async (id) => {
    await axios.delete(`${process.env.REACT_APP_API_URL}/api/pets/${id}`);
    fetchPets();
  };

  return (
    <div>
      <h2>Admin Dashboard</h2>
      <AddPet onPetAdded={fetchPets} />
      <ul>
        {pets.map((pet) => (
          <li key={pet._id} className="admin-pet-item">
            <img src={pet.image} alt={pet.name} className="admin-pet-img" />
            <strong>{pet.name}</strong> - {pet.breed}
            <button onClick={() => setEditingPetId(pet._id)}>Edit</button>
            <button onClick={() => handleDelete(pet._id)}>Delete</button>
          </li>
        ))}
      </ul>
      {editingPetId && (
        <EditPet
          petId={editingPetId}
          onUpdateDone={() => {
            setEditingPetId(null);
            fetchPets();
          }}
        />
      )}
    </div>
  );
};

export default AdminDashboard;
