import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Home.css';
import { motion } from 'framer-motion';

const Home = () => {
  const [pets, setPets] = useState([]);
  const [filteredPets, setFilteredPets] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const fetchPets = async () => {
      const res = await axios.get('http://localhost:5000/api/pets');
      setPets(res.data);
      setFilteredPets(res.data);
    };

    const fetchCategories = async () => {
      const res = await axios.get('http://localhost:5000/api/categories');
      setCategories(res.data);
    };

    fetchPets();
    fetchCategories();
  }, []);

  const handleCategoryChange = (cat) => {
    setSelectedCategory(cat);
    if (cat === 'All') {
      setFilteredPets(pets);
    } else {
      const filtered = pets.filter(pet => pet.category?.name === cat);
      setFilteredPets(filtered);
    }
  };

  return (
    <div className="home-container">
      <h2>Available Pets for Adoption</h2>

      <div className="category-buttons">
        <button onClick={() => handleCategoryChange('All')} className={selectedCategory === 'All' ? 'active' : ''}>All</button>
        {categories.map((cat) => (
          <button key={cat._id} onClick={() => handleCategoryChange(cat.name)} className={selectedCategory === cat.name ? 'active' : ''}>
            {cat.name}
          </button>
        ))}
      </div>

      <div className="card-grid">
        {filteredPets.map((pet) => (
          <motion.div
            layout
            initial={{ opacity: 0, scale: 0.95 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.4 }}
            className="pet-card"
            key={pet._id}
          >
            <img src={pet.image} alt={pet.name} />
            <h3>{pet.name}</h3>
            <p>Breed: {pet.breed}</p>
            <p>Age: {pet.age} years</p>
            <p>Gender: {pet.gender || 'Unknown'}</p>
            <button>Adopt</button>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default Home;
